/* eslint-disable react/destructuring-assignment */
/* eslint-disable react/prop-types */
/* eslint-disable react/button-has-type */
import React,{useState} from 'react';
import styled from 'styled-components';
import {NavLink,useHistory} from "react-router-dom";
import axios from 'axios';
function Post(props) {
  const history=useHistory();
  const [upvotes,setUpvotes]= useState('');
  const [downvotes,setDownvotes]=useState('');
const [comments,setComments]=useState('');
  const [owner,setOwner]= useState('');
  const [question,setQuestion]=useState('');

  const postquestion=async (e) =>{
      e.preventDefault();

     const form_data=new FormData();
     form_data.append('question',question);
     const url='http://localhost:5000/api/askquestion'
     try {
      const res=await axios.post(url,form_data,{
          withCredentials:true,
      });
     
      alert(res.data.msg);
  } catch (error) {
      window.alert("Error");
  }
    //  const data=res.json();
      
    //  if(res.status==400 || !data)
    //  {
    //      window.alert("Invalid Credentials");
    //  }
    //  else{
    //      window.alert("Question Uploded Successfully");
    //      history.push('/Homepage');
    //  }
    }
  // eslint-disable-next-line react/prop-types
  return (props.trigger) ? (

    <Contain>
      <div>
      <h5 value={question} onChange={(e)=>setOwner(e.target.value)} >Sanket Kulkrani</h5>
        <button type="reset" onClick={() => props.setTrigger(false)}>
          close
        </button>
        <input type="text" value={question} onChange={(e)=>setQuestion(e.target.value)} placeholder="Enter Question" />
        
        <button type="submit" onClick={postquestion} >
          post
        </button>
        {props.children}
      </div>
    </Contain>
  ) : '';
}

export default Post;
const Contain = styled.div`
width: 1000px;
height: 300px;
position: absolute;

border: 1px solid lightgray;
background-color: white;
box-shadow: 2rem 1.4rem 2rem lightgray;
top: 130%;
left: 25%;
`;
