import React from 'react';
import { useState } from 'react';
import Post from './Post';
import styled from 'styled-components';
import { BrowserRouter as Router ,Route} from 'react-router-dom';
import Questionlist from './QuestionList';
import TrendingTask from './QaBooks'
export default function Qa() {
  const [Popup, setPopup] = useState(false);
  return (

  <Router>
    <QASection >
    <PostT onClick={() => setPopup(true)}><Name>User Name</Name><AskQ>What is your task/queston?</AskQ></PostT>
    <Post trigger={Popup} setTrigger={setPopup}>
      </Post>
      <Route path="/Homepage">
      <Questionlist />
      </Route>
      <Route path="/trendingTask">
      <TrendingTask />
      </Route>
      </QASection>
      </Router>
  );
}
const QASection= styled.div`


position: absolute;
width: 80%;
height: 912px;
left: 280px;
top: 120px;

background-repeat: no-repeat;
background-size: cover;
`
const PostT = styled.div`
display: flex;
&:hover {
    cursor: pointer;
  }
flex-direction: column;
font-family: Poppins;
font-style: normal;
height: 102px;
background-color:#FAF8F9;
font-size: 18.9296px;
padding-left: 20px;
margin:1rem;
align-items: space-between;
justify-content: space-evenly;
`;
const Name = styled.text`

font-family: Poppins;
font-style: normal;
font-weight: 800;
font-size: 18px;
`;
const AskQ = styled.text`

font-family: Poppins;
font-style: normal;
font-weight: 500;
font-size: 18px;
color: #766D6D;

`;
